import styled from "styled-components";
import React, {useState} from 'react';
const Container = styled.div`
display: flex;
flex-direction: column;
align-items: center;
margin: 10px;
font-family: Oswald;
width: 100%;
`;

const BalanceBox = styled.div`
  font-size: 18px;
  width: 100%;
  font-weight: bold;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  `;

const AddTransaction = styled.div`
  background: black;
  color: white;
  padding: 6px 10px;
  border-radius: 4px;
  text-align: center;
  cursor: pointer;
  font-weight: bold;
  font-size: x-small;
  `;

const AddTransactionContainer = styled.div`
  display: flex;
  flex-direction: column;
  border: 1px solid #e6e8e9;
  gap: 10px;
  width: 100%;
  padding: 15px 20px;
  margin: 20px;
  & input {
    outline: none;
    padding: 10px 12px;
    border-radius: 4px;
    border: 1px solid #e6e8e9;
    }
  `;
  const RadioBox = styled.div`
    display: flex;
    flex-direction: row;
    width: 100%;
    algin-items: clearInterval;
    & input {
    width: unset;
    margin: 0 10px;
    }
    `;

  const AddTransactionView = (props) => {
    const [amount, setAmount] = useState();
    const [desc, setDesc] = useState();
    const [type, setType] = useState("EXPENSE");

    const addTransaction =() => {
        console.log({amount,desc,type});
        props.toggleAddTxn();
    };
    return(
         <AddTransactionContainer>
            <input placeholder = "Amount" value={amount} onChange={(e)=>setAmount(e.target.value)}/>
            <input placeholder = "Description"
            value={desc} 
            onChange={(e)=>setDesc(e.target.value)}/>
            <RadioBox>
                <input type="radio" id="expense" name="type" value="EXPENSE" checked={type==="EXPENSE"}
                onChange={(e)=>setType(e.target.value)}
                />
                <label htmlFor="expense">Expense</label>
                <input type="radio" id="income" name="type" value="INCOME" checked={type==="INCOME"}
                onChange={(e)=>setType(e.target.value)}
                />
                <label htmlFor="income">Income</label>
            </RadioBox>
            <AddTransaction onClick={addTransaction}>Add Transaction</AddTransaction>

    </AddTransactionContainer>
    );
  };


const OverviewComponent = (props) => {
    const [isAddTxnVisible, toggleAddTxn] = useState(false);
    return(
    <Container>
        <BalanceBox>
            Balance: $10000
            <AddTransaction onClick={() => toggleAddTxn(!isAddTxnVisible)}>
            {isAddTxnVisible ? "Cancel" : "ADD"}
            </AddTransaction>
        </BalanceBox>
        {isAddTxnVisible && <AddTransactionView toggleAddTxn/>}
    </Container>


);
    
};
export default OverviewComponent
